import React from 'react'
import DateNow from './DateNow'

const NowDay = ({ year, month, day, isPlaned, nowDayTask, taskArray }) => {
  return (
    <div className='nowday'>
      <DateNow year={year} month={month} day={day}/>
      {
        Number(nowDayTask.slice(0,2)) === day
        ? <>
            <h1 className='nowday-title'>Дела на сегодня</h1>
            <ul className='nowday__list'>
              {
                taskArray !== null && taskArray !== undefined
                ? taskArray.map(el =>
                    <li key={el.taskId} className='tasks-item' style={el.isCompleted === true ? {textDecoration: 'line-through'} : {}}>{el.taskId}. {el.text}</li>
                  )
                
                : <li className='tasks-item'></li>
              }
              <li className='nowday__item'>{}</li>
            </ul>
          </>
        : <h1 className='nowday-title'>Дел на сегодня не запланировано</h1>
      }
    </div>
  )
}

export default NowDay
